#!/usr/bin/env python3\n\"\"\"\nEcoPlan Server Startup Script\n\"\"\"\n\nimport subprocess\nimport sys\nimport time\nimport webbrowser\nfrom pathlib import Path\n\ndef main():\n    print(\"üå± EcoPlan - AI Urban Planning Assistant\")\n    print(\"=\"*50)\n    print(\"Starting server...\")\n    \n    try:\n        # Start Flask server\n        process = subprocess.Popen(\n            [sys.executable, \"app.py\"],\n            cwd=Path(__file__).parent\n        )\n        \n        # Wait for server to start\n        time.sleep(3)\n        \n        print(\"\\n‚úÖ Server started successfully!\")\n        print(\"\\nüìç Available URLs:\")\n        print(\"   Main App: http://localhost:5000\")\n        print(\"   Manual Input: http://localhost:5000/manual_input\")\n        print(\"   Autocomplete Demo: http://localhost:5000/autocomplete_demo\")\n        print(\"   Test City Fetch: http://localhost:5000/test_city_fetch\")\n        \n        print(\"\\nüîß API Endpoints:\")\n        print(\"   City Suggestions: http://localhost:5000/api/city_suggestions/<query>\")\n        print(\"   City Data: http://localhost:5000/api/fetch_city_data/<city_name>\")\n        \n        print(\"\\nüí° Try typing 'coim' in the city input to see Coimbatore suggestions!\")\n        print(\"\\nPress Ctrl+C to stop the server...\")\n        \n        # Open browser to demo page\n        time.sleep(1)\n        webbrowser.open('http://localhost:5000/autocomplete_demo')\n        \n        # Wait for process\n        process.wait()\n        \n    except KeyboardInterrupt:\n        print(\"\\nüõë Stopping server...\")\n        if 'process' in locals():\n            process.terminate()\n            process.wait()\n        print(\"Server stopped.\")\n    except Exception as e:\n        print(f\"‚ùå Error: {e}\")\n\nif __name__ == \"__main__\":\n    main()"